import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CreateRecipe.css';

const CreateRecipe = () => {
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState({
    name: '',
    description: '',
    ingredients: [],
    instructions: '',
    imageUrl: '',
    cookingTime: 0
  });
  const [newIngredient, setNewIngredient] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setRecipe(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddIngredient = () => {
    if (newIngredient.trim()) {
      setRecipe(prev => ({
        ...prev,
        ingredients: [...prev.ingredients, newIngredient.trim()]
      }));
      setNewIngredient('');
    }
  };

  const handleRemoveIngredient = (index) => {
    setRecipe(prev => ({
      ...prev,
      ingredients: prev.ingredients.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically make an API call to save the recipe
    // For now, we'll just store it in localStorage
    const existingRecipes = JSON.parse(localStorage.getItem('recipes') || '[]');
    const newRecipe = {
      ...recipe,
      id: Date.now(), // Simple way to generate unique ID
      date: new Date().toISOString()
    };
    
    localStorage.setItem('recipes', JSON.stringify([...existingRecipes, newRecipe]));
    navigate('/home'); // Redirect to home page after creation
  };

  const handleSave = () => {
    // Save to "Saved Recipes"
    const savedRecipes = JSON.parse(localStorage.getItem('savedRecipes') || '[]');
    const recipeToSave = {
      ...recipe,
      id: Date.now(),
      date: new Date().toISOString()
    };
    
    localStorage.setItem('savedRecipes', JSON.stringify([...savedRecipes, recipeToSave]));
    alert('Recipe saved successfully!');
  };

  return (
    <div className="create-recipe-container">
      <div className="recipe-form-card">
        <h2>Create Recipe</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Name</label>
            <input
              type="text"
              name="name"
              value={recipe.name}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Description</label>
            <textarea
              name="description"
              value={recipe.description}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Ingredients</label>
            <div className="ingredients-input">
              <input
                type="text"
                value={newIngredient}
                onChange={(e) => setNewIngredient(e.target.value)}
                placeholder="Enter an ingredient"
              />
              <button type="button" onClick={handleAddIngredient} className="add-ingredient-btn">
                Add Ingredient
              </button>
            </div>
            <ul className="ingredients-list">
              {recipe.ingredients.map((ingredient, index) => (
                <li key={index}>
                  {ingredient}
                  <button 
                    type="button" 
                    onClick={() => handleRemoveIngredient(index)}
                    className="remove-ingredient-btn"
                  >
                    ×
                  </button>
                </li>
              ))}
            </ul>
          </div>

          <div className="form-group">
            <label>Instructions</label>
            <textarea
              name="instructions"
              value={recipe.instructions}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Image URL</label>
            <input
              type="url"
              name="imageUrl"
              value={recipe.imageUrl}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Cooking Time (minutes)</label>
            <input
              type="number"
              name="cookingTime"
              value={recipe.cookingTime}
              onChange={handleInputChange}
              min="0"
              required
            />
          </div>

          <div className="button-group">
            <button type="submit" className="create-btn">Create Recipe</button>
            <button type="button" onClick={handleSave} className="save-btn">Save Recipe</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateRecipe;
